mapper.select:
SELECT b.book_id, b.writer_id, b.category_id, b.title, b.status, b.current_sequence, b.max_sequence, b.created_at FROM books b
AND b.status = #{request.status}
AND b.category_id = #{request.categoryId}
AND b.title LIKE CONCAT('%', #{request.keyword}, '%')
ORDER BY b.title
ORDER BY b.created_at
ASC
DESC
LIMIT #{request.size} OFFSET #{request.offset}

SELECT COUNT(*)
FROM books b


        AND b.status = #{request.status}


        AND b.category_id = #{request.categoryId}


        AND b.title LIKE CONCAT('%', #{request.keyword}, '%')
